import React, { useState, useEffect } from "react";
import orderDetailsApi from "../../api/orderDetailsApi";
import "./orderDetails.css";

const OrderDetailsForm = ({ order, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    customerId: "",
    shopId: "",
    dateOfPurchase: "",
    total: "",
    paymentMode: "",
  });

  useEffect(() => {
    if (order) {
      setFormData({
        customerId: order.customer?.id || "",
        shopId: order.shop?.shopId || "",
        dateOfPurchase: order.dateOfPurchase
          ? new Date(order.dateOfPurchase).toISOString().substring(0, 16) // for datetime-local input
          : "",
        total: order.total || "",
        paymentMode: order.paymentMode || "",
      });
    } else {
      setFormData({
        customerId: "",
        shopId: "",
        dateOfPurchase: "",
        total: "",
        paymentMode: "",
      });
    }
  }, [order]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare payload with nested customer and shop objects as backend expects
    const payload = {
      dateOfPurchase: formData.dateOfPurchase,
      total: parseFloat(formData.total),
      paymentMode: formData.paymentMode,
      customer: { id: parseInt(formData.customerId) },
      shop: { shopId: parseInt(formData.shopId) },
    };

    try {
      if (order && order.id) {
        await orderDetailsApi.updateOrder(order.id, payload);
      } else {
        await orderDetailsApi.addOrder(payload);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving order:", error);
    }
  };

  return (
    <div className="order-form-overlay">
      <form className="order-form" onSubmit={handleSubmit}>
        <h3>{order ? "Edit Order" : "Add Order"}</h3>

        <label>
          Customer ID:
          <input
            type="number"
            name="customerId"
            value={formData.customerId}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <label>
          Shop ID:
          <input
            type="number"
            name="shopId"
            value={formData.shopId}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <label>
          Date of Purchase:
          <input
            type="datetime-local"
            name="dateOfPurchase"
            value={formData.dateOfPurchase}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Total:
          <input
            type="number"
            step="0.01"
            name="total"
            value={formData.total}
            onChange={handleChange}
            required
            min="0"
          />
        </label>

        <label>
          Payment Mode:
          <input
            type="text"
            name="paymentMode"
            value={formData.paymentMode}
            onChange={handleChange}
            required
          />
        </label>

        <div className="form-buttons">
          <button type="submit">{order ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default OrderDetailsForm;
