import React, { useState, useEffect } from "react";
import shopApi from "../../api/shopApi";
import shopOwnerApi from "../../api/shopOwnerApi";
import mallApi from "../../api/mallApi";
import employeeApi from "../../api/employeeApi"; // new: for employees
import "./shop.css";

const ShopForm = ({ shop, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    shopName: "",
    shopCategory: "",
    customers: "",
    shopStatus: "",
    leaseStatus: "",
    shopOwnerId: "",
    mallId: "",
    shopEmployeeId: "", // new
  });

  const [shopOwners, setShopOwners] = useState([]);
  const [malls, setMalls] = useState([]);
  const [employees, setEmployees] = useState([]); // new

  useEffect(() => {
    shopOwnerApi.getAllShopOwners()
      .then(res => setShopOwners(res.data))
      .catch(() => alert("Failed to load shop owners"));

    mallApi.getAllMalls()
      .then(res => setMalls(res.data))
      .catch(() => alert("Failed to load malls"));

    employeeApi.getAllEmployees() // new
      .then(res => setEmployees(res.data))
      .catch(() => alert("Failed to load employees"));
  }, []);

  useEffect(() => {
    if (shop) {
      setFormData({
        shopName: shop.shopName || "",
        shopCategory: shop.shopCategory || "",
        customers: shop.customers || "",
        shopStatus: shop.shopStatus || "",
        leaseStatus: shop.leaseStatus || "",
        shopOwnerId: shop.shopOwner ? shop.shopOwner.id : "",
        mallId: shop.mall ? shop.mall.id : "",
        shopEmployeeId: shop.shopEmployee ? shop.shopEmployee.id : "", // new
      });
    } else {
      setFormData({
        shopName: "",
        shopCategory: "",
        customers: "",
        shopStatus: "",
        leaseStatus: "",
        shopOwnerId: "",
        mallId: "",
        shopEmployeeId: "", // new
      });
    }
  }, [shop]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

 const handleSubmit = async (e) => {
  e.preventDefault();

  if (!formData.shopName || !formData.shopOwnerId || !formData.mallId) {
    alert("Please fill in required fields: Shop Name, Shop Owner, and Mall.");
    return;
  }

  // Prepare payload with flat IDs and correct types
  const payload = {
    shopName: formData.shopName,
    shopCategory: formData.shopCategory,
    customers: formData.customers ? Number(formData.customers) : 0,
    shopStatus: formData.shopStatus,
    leaseStatus: formData.leaseStatus,
    shopOwnerId: Number(formData.shopOwnerId),
    mallId: Number(formData.mallId),
    shopEmployeeId: formData.shopEmployeeId ? Number(formData.shopEmployeeId) : null,
  };

  try {
    if (shop && shop.shopId) {
      await shopApi.updateShop(shop.shopId, payload);
    } else {
      await shopApi.addShop(payload);
    }
    onSubmit();
  } catch (error) {
    console.error("Error saving shop:", error);
    alert("Failed to save shop. Please check your input.");
  }
};


  return (
    <div className="shop-form-overlay">
      <form className="shop-form" onSubmit={handleSubmit}>
        <h3>{shop ? "Edit Shop" : "Add Shop"}</h3>

        {/* Shop Name */}
        <label>
          Shop Name*:
          <input
            type="text"
            name="shopName"
            value={formData.shopName}
            onChange={handleChange}
            required
          />
        </label>

        {/* Shop Category */}
        <label>
          Shop Category:
          <input
            type="text"
            name="shopCategory"
            value={formData.shopCategory}
            onChange={handleChange}
          />
        </label>

        {/* Customers */}
        <label>
          Customers:
          <input
            type="number"
            name="customers"
            value={formData.customers}
            onChange={handleChange}
            min="0"
          />
        </label>

        {/* Shop Status */}
        <label>
          Shop Status:
          <input
            type="text"
            name="shopStatus"
            value={formData.shopStatus}
            onChange={handleChange}
          />
        </label>

        {/* Lease Status */}
        <label>
          Lease Status:
          <input
            type="text"
            name="leaseStatus"
            value={formData.leaseStatus}
            onChange={handleChange}
          />
        </label>

        {/* Shop Owner */}
        <label>
          Shop Owner*:
          <select
            name="shopOwnerId"
            value={formData.shopOwnerId}
            onChange={handleChange}
            required
          >
            <option value="">Select Shop Owner</option>
            {shopOwners.map(owner => (
              <option key={owner.id} value={owner.id}>{owner.name}</option>
            ))}
          </select>
        </label>

        {/* Mall */}
        <label>
          Mall*:
          <select
            name="mallId"
            value={formData.mallId}
            onChange={handleChange}
            required
          >
            <option value="">Select Mall</option>
            {malls.map(mall => (
              <option key={mall.id} value={mall.id}>{mall.mallName}</option>
            ))}
          </select>
        </label>

        {/* Shop Employee (optional) */}
        <label>
          Shop Employee:
          <select
            name="shopEmployeeId"
            value={formData.shopEmployeeId}
            onChange={handleChange}
          >
            <option value="">Select Employee (optional)</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>{emp.name}</option>
            ))}
          </select>
        </label>

        <div className="form-buttons">
          <button type="submit">{shop ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default ShopForm;
