// src/components/shopowners/ShopOwnerList.js
import React, { useEffect, useState } from "react";
import shopOwnerApi from "../../api/shopOwnerApi";
import ShopOwnerForm from "./ShopOwnerForm";
import "./shopowner.css";

const ShopOwnerList = () => {
  const [shopOwners, setShopOwners] = useState([]);
  const [editingShopOwner, setEditingShopOwner] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchShopOwners = async () => {
    try {
      const response = await shopOwnerApi.getAllShopOwners();
      setShopOwners(response.data);
    } catch (error) {
      console.error("Failed to fetch shop owners:", error);
    }
  };

  useEffect(() => {
    fetchShopOwners();
  }, []);

  const handleEdit = (shopOwner) => {
    setEditingShopOwner(shopOwner);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this shop owner?")) {
      try {
        await shopOwnerApi.deleteShopOwner(id);
        fetchShopOwners();
      } catch (error) {
        console.error("Failed to delete shop owner:", error);
      }
    }
  };

  const handleFormClose = () => {
    setEditingShopOwner(null);
    setShowForm(false);
  };

  const handleFormSubmit = () => {
    fetchShopOwners();
    handleFormClose();
  };

  return (
    <div className="shopowner-container">
      <h2>Shop Owner List</h2>
      <button onClick={() => setShowForm(true)}>Add Shop Owner</button>

      {showForm && (
        <ShopOwnerForm
          shopOwner={editingShopOwner}
          onClose={handleFormClose}
          onSubmit={handleFormSubmit}
        />
      )}

      <table className="shopowner-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Contact Number</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {shopOwners.map(({ id, name, contactNumber, email }) => (
            <tr key={id}>
              <td>{id}</td>
              <td>{name}</td>
              <td>{contactNumber}</td>
              <td>{email}</td>
              <td>
                <button onClick={() => handleEdit({ id, name, contactNumber, email })}>
                  Edit
                </button>
                <button onClick={() => handleDelete(id)} className="delete-btn">
                  Delete
                </button>
              </td>
            </tr>
          ))}
          {shopOwners.length === 0 && (
            <tr>
              <td colSpan="5">No shop owners found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ShopOwnerList;
