import React, { useState, useEffect } from "react";
import itemApi from "../../api/itemApi";
import mallApi from "../../api/mallApi"; // You need to implement this to fetch malls
import "./item.css";

export default function ItemForm({ item, onClose }) {
  const [formData, setFormData] = useState({
    name: "",
    manufacturing: "",
    expiry: "",
    price: "",
    category: "",
    mallId: "",  // We'll store mall's ID for selection
  });
  const [malls, setMalls] = useState([]);

  // Fetch malls for dropdown
  useEffect(() => {
    mallApi.getAllMalls()
      .then(res => setMalls(res.data))
      .catch(() => alert("Failed to load malls"));
  }, []);

  // Populate form on edit
  useEffect(() => {
    if (item) {
      setFormData({
        name: item.name || "",
        manufacturing: item.manufacturing || "",
        expiry: item.expiry || "",
        price: item.price || "",
        category: item.category || "",
        mallId: item.mall ? item.mall.id : "",
      });
    }
  }, [item]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.name || !formData.price || !formData.mallId) {
      alert("Please fill in required fields: Name, Price, and Mall.");
      return;
    }

    // Prepare payload with mall object having only ID
    const payload = {
      ...formData,
      price: parseFloat(formData.price),
      mall: { id: formData.mallId },
    };

    // Remove mallId as it's replaced by mall object
    delete payload.mallId;

    if (item) {
      itemApi.updateItem(item.id, payload)
        .then(() => {
          alert("Item updated successfully.");
          onClose();
        })
        .catch(() => alert("Failed to update item."));
    } else {
      itemApi.addItem(payload)
        .then(() => {
          alert("Item added successfully.");
          onClose();
        })
        .catch(() => alert("Failed to add item."));
    }
  };

  return (
    <div className="form-container">
      <h3>{item ? "Edit Item" : "Add New Item"}</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Name*:
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </label>

        <label>
          Manufacturing Date:
          <input type="date" name="manufacturing" value={formData.manufacturing} onChange={handleChange} />
        </label>

        <label>
          Expiry Date:
          <input type="date" name="expiry" value={formData.expiry} onChange={handleChange} />
        </label>

        <label>
          Price*:
          <input type="number" step="0.01" name="price" value={formData.price} onChange={handleChange} />
        </label>

        <label>
          Category:
          <input type="text" name="category" value={formData.category} onChange={handleChange} />
        </label>

        <label>
          Mall*:
          <select name="mallId" value={formData.mallId} onChange={handleChange}>
            <option value="">Select Mall</option>
            {malls.map(mall => (
              <option key={mall.id} value={mall.id}>{mall.name}</option>
            ))}
          </select>
        </label>

        <div className="form-buttons">
          <button type="submit">{item ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
