import React, { useState, useEffect } from "react";
import mallApi from "../../api/mallApi";
import "./mall.css";

export default function MallForm({ mallId, onSuccess, onCancel }) {
  const [mall, setMall] = useState({
    mallName: "",
    location: "",
    categories: "",
  });

  const [error, setError] = useState(null);

  // Load mall details for editing
  useEffect(() => {
    if (mallId) {
      mallApi
        .getMallById(mallId)
        .then((res) => {
          // response data structure assumed to match model directly
          setMall({
            mallName: res.data.mallName || "",
            location: res.data.location || "",
            categories: res.data.categories || "",
          });
        })
        .catch(() => setError("Failed to load mall details"));
    }
  }, [mallId]);

  // Update form state on input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setMall((prev) => ({ ...prev, [name]: value }));
  };

  // Submit form to add or update mall
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Basic validation
    if (!mall.mallName.trim() || !mall.location.trim()) {
      setError("Mall Name and Location are required.");
      return;
    }

    try {
      if (mallId) {
        // Update existing mall
        await mallApi.updateMall(mallId, mall);
      } else {
        // Add new mall
        await mallApi.addMall(mall);
      }
      onSuccess();
    } catch {
      setError("Failed to save mall. Please try again.");
    }
  };

  return (
    <div className="mall-form-container">
      <h2>{mallId ? "Edit Mall" : "Add Mall"}</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="mallName">Mall Name*:</label>
          <input
            type="text"
            id="mallName"
            name="mallName"
            value={mall.mallName}
            onChange={handleChange}
            required
            placeholder="Enter mall name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="location">Location*:</label>
          <input
            type="text"
            id="location"
            name="location"
            value={mall.location}
            onChange={handleChange}
            required
            placeholder="Enter location"
          />
        </div>

        <div className="form-group">
          <label htmlFor="categories">Categories:</label>
          <textarea
            id="categories"
            name="categories"
            value={mall.categories}
            onChange={handleChange}
            placeholder="Enter categories or description"
            rows={4}
          />
        </div>

        <div className="form-buttons">
          <button type="submit">{mallId ? "Update" : "Add"}</button>{" "}
          <button type="button" onClick={onCancel}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
