import React, { useState, useEffect } from "react";
import shopOwnerApi from "../../api/shopOwnerApi";
import mallApi from "../../api/mallApi";
import employeeApi from "../../api/employeeApi";
import shopApi from "../../api/shopApi";
import "./shop.css";

const ShopForm = ({ shop, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    shopName: "",
    shopCategory: "",
    customers: "",
    shopStatus: "",
    leaseStatus: "",
    shopOwnerId: "",
    mallId: "",
    shopEmployeeId: "",
  });

  const [shopOwners, setShopOwners] = useState([]);
  const [malls, setMalls] = useState([]);
  const [employees, setEmployees] = useState([]);

  // Load dropdown data once
  useEffect(() => {
    shopOwnerApi.getAllShopOwners()
      .then(res => setShopOwners(res.data))
      .catch(() => alert("Failed to load shop owners"));

    mallApi.getAllMalls()
      .then(res => setMalls(res.data))
      .catch(() => alert("Failed to load malls"));

    employeeApi.getAllEmployees()
      .then(res => setEmployees(res.data))
      .catch(() => alert("Failed to load employees"));
  }, []);

  // Load form data if editing existing shop
  useEffect(() => {
    if (shop) {
      setFormData({
        shopName: shop.shopName || "",
        shopCategory: shop.shopCategory || "",
        customers: shop.customers !== undefined ? shop.customers : "",
        shopStatus: shop.shopStatus || "",
        leaseStatus: shop.leaseStatus || "",
        shopOwnerId: shop.shopOwner ? shop.shopOwner.id : "",
        mallId: shop.mall ? shop.mall.id : "",
        shopEmployeeId: shop.shopEmployee ? shop.shopEmployee.id : "",
      });
    } else {
      // Reset form for new shop
      setFormData({
        shopName: "",
        shopCategory: "",
        customers: "",
        shopStatus: "",
        leaseStatus: "",
        shopOwnerId: "",
        mallId: "",
        shopEmployeeId: "",
      });
    }
  }, [shop]);

  // Handle input/select changes
  const handleChange = (e) => {
    const { name, value } = e.target;

    // For customers (numeric), optionally enforce number only
    if (name === "customers") {
      // allow only numbers or empty string
      if (value === "" || /^[0-9\b]+$/.test(value)) {
        setFormData(prev => ({ ...prev, [name]: value }));
      }
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  // Submit form handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate required fields
    if (!formData.shopName.trim() || !formData.shopOwnerId || !formData.mallId) {
      alert("Please fill in required fields: Shop Name, Shop Owner, and Mall.");
      return;
    }

    // Prepare payload as backend expects nested objects with IDs
    const payload = {
      shopName: formData.shopName.trim(),
      shopCategory: formData.shopCategory.trim() || null,
      customers: formData.customers ? parseInt(formData.customers, 10) : null,
      shopStatus: formData.shopStatus.trim() || null,
      leaseStatus: formData.leaseStatus.trim() || null,
      shopOwner: { id: formData.shopOwnerId },
      mall: { id: formData.mallId },
      shopEmployee: formData.shopEmployeeId ? { id: formData.shopEmployeeId } : null,
    };

    try {
      if (shop && shop.shopId) {
        await shopApi.updateShop(shop.shopId, payload);
      } else {
        await shopApi.addShop(payload);
      }
      onSubmit();  // refresh list or close form externally
    } catch (error) {
      console.error("Error saving shop:", error);
      alert("Failed to save shop. Please check your inputs and try again.");
    }
  };

  return (
    <div className="shop-form-overlay">
      <form className="shop-form" onSubmit={handleSubmit}>
        <h3>{shop ? "Edit Shop" : "Add Shop"}</h3>

        <label>
          Shop Name*:
          <input
            type="text"
            name="shopName"
            value={formData.shopName}
            onChange={handleChange}
            required
            autoFocus
          />
        </label>

        <label>
          Shop Category:
          <input
            type="text"
            name="shopCategory"
            value={formData.shopCategory}
            onChange={handleChange}
          />
        </label>

        <label>
          Customers:
          <input
            type="number"
            name="customers"
            value={formData.customers}
            onChange={handleChange}
            min="0"
          />
        </label>

        <label>
          Shop Status:
          <input
            type="text"
            name="shopStatus"
            value={formData.shopStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Lease Status:
          <input
            type="text"
            name="leaseStatus"
            value={formData.leaseStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Shop Owner*:
          <select
            name="shopOwnerId"
            value={formData.shopOwnerId}
            onChange={handleChange}
            required
          >
            <option value="">Select Shop Owner</option>
            {shopOwners.map(owner => (
              <option key={owner.id} value={owner.id}>{owner.name}</option>
            ))}
          </select>
        </label>

        <label>
          Mall*:
          <select
            name="mallId"
            value={formData.mallId}
            onChange={handleChange}
            required
          >
            <option value="">Select Mall</option>
            {malls.map(mall => (
              <option key={mall.id} value={mall.id}>{mall.mallName}</option>
            ))}
          </select>
        </label>

        <label>
          Shop Employee:
          <select
            name="shopEmployeeId"
            value={formData.shopEmployeeId}
            onChange={handleChange}
          >
            <option value="">Select Employee (optional)</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>{emp.name}</option>
            ))}
          </select>
        </label>

        <div className="form-buttons">
          <button type="submit">{shop ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default ShopForm;
