import React, { useEffect, useState } from "react";
import itemApi from "../../api/itemApi";
import ItemForm from "./ItemForm";
import "./item.css";

export default function ItemList() {
  const [items, setItems] = useState([]);
  const [editingItem, setEditingItem] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchItems = () => {
    itemApi.getAllItems()
      .then((res) => setItems(res.data))
      .catch(() => alert("Failed to load items"));
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleEdit = (item) => {
    setEditingItem(item);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm("Delete this item?")) {
      itemApi.deleteItem(id)
        .then(fetchItems)
        .catch(() => alert("Failed to delete item"));
    }
  };

  const handleAdd = () => {
    setEditingItem(null);
    setShowForm(true);
  };

  const handleFormClose = () => {
    setShowForm(false);
    fetchItems();
  };

  return (
    <div>
      <h2>Items</h2>
      <button onClick={handleAdd}>Add New Item</button>
      {showForm && <ItemForm item={editingItem} onClose={handleFormClose} />}
      <table>
        <thead>
          <tr>
            <th>Item ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>Price</th>
            <th>Shop ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.length === 0 && (
            <tr><td colSpan="6">No items found.</td></tr>
          )}
          {items.map(({ id, name, description, price, shopId }) => (
            <tr key={id}>
              <td>{id}</td>
              <td>{name}</td>
              <td>{description}</td>
              <td>{price}</td>
              <td>{shopId}</td>
              <td>
                <button onClick={() => handleEdit({ id, name, description, price, shopId })}>Edit</button>
                <button onClick={() => handleDelete(id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
