import React, { useEffect, useState } from "react";
import mallApi from "../../api/mallApi";
import MallForm from "./MallForm";
import "./mall.css";

export default function MallList() {
  const [malls, setMalls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // For Add/Edit form
  const [editingMallId, setEditingMallId] = useState(null);
  const [showForm, setShowForm] = useState(false);

  // Fetch malls list
  const fetchMalls = async () => {
    try {
      setLoading(true);
      const response = await mallApi.getAllMalls();
      setMalls(response.data);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch malls");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMalls();
  }, []);

  // Delete mall
  const deleteMall = async (id) => {
    if (window.confirm("Are you sure to delete this mall?")) {
      try {
        await mallApi.deleteMall(id);
        fetchMalls();
      } catch {
        alert("Failed to delete mall");
      }
    }
  };

  // Open form for adding new mall
  const handleAddMall = () => {
    setEditingMallId(null);
    setShowForm(true);
  };

  // Open form for editing mall
  const handleEditMall = (id) => {
    setEditingMallId(id);
    setShowForm(true);
  };

  // After form success (add or update)
  const handleFormSuccess = () => {
    setShowForm(false);
    fetchMalls();
  };

  // Cancel form
  const handleFormCancel = () => {
    setShowForm(false);
  };

  if (loading) return <p>Loading malls...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Malls</h2>

      {showForm ? (
        <MallForm
          mallId={editingMallId}
          onSuccess={handleFormSuccess}
          onCancel={handleFormCancel}
        />
      ) : (
        <>
          <table
            border="1"
            cellPadding="8"
            cellSpacing="0"
            style={{ width: "100%", borderCollapse: "collapse" }}
          >
            <thead>
              <tr>
                <th>ID</th>
                <th>Mall Name</th>
                <th>Location</th>
                <th>Categories</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {malls.length === 0 && (
                <tr>
                  <td colSpan="5">No malls found</td>
                </tr>
              )}
              {malls.map((mall) => (
                <tr key={mall.id}>
                  <td>{mall.id}</td>
                  <td>{mall.mallName}</td>
                  <td>{mall.location}</td>
                  <td>{mall.categories}</td>
                  <td>
                    <button onClick={() => handleEditMall(mall.id)}>Edit</button>{" "}
                    <button onClick={() => deleteMall(mall.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button onClick={handleAddMall} style={{ marginTop: "1rem" }}>
            Add New Mall
          </button>
        </>
      )}
    </div>
  );
}
