// src/components/orderdetails/OrderDetailsForm.js
import React, { useState, useEffect } from "react";
import orderDetailsApi from "../../api/orderDetailsApi";
import "./orderDetails.css";

const OrderDetailsForm = ({ order, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    customerId: "",
    shopId: "",
    itemId: "",
    quantity: "",
    orderDate: "",
  });

  useEffect(() => {
    if (order) {
      setFormData({
        customerId: order.customerId || "",
        shopId: order.shopId || "",
        itemId: order.itemId || "",
        quantity: order.quantity || "",
        orderDate: order.orderDate
          ? new Date(order.orderDate).toISOString().substring(0, 10)
          : "",
      });
    } else {
      setFormData({
        customerId: "",
        shopId: "",
        itemId: "",
        quantity: "",
        orderDate: "",
      });
    }
  }, [order]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (order && order.id) {
        await orderDetailsApi.updateOrder(order.id, formData);
      } else {
        await orderDetailsApi.addOrder(formData);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving order:", error);
    }
  };

  return (
    <div className="order-form-overlay">
      <form className="order-form" onSubmit={handleSubmit}>
        <h3>{order ? "Edit Order" : "Add Order"}</h3>

        <label>
          Customer ID:
          <input
            type="number"
            name="customerId"
            value={formData.customerId}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <label>
          Shop ID:
          <input
            type="number"
            name="shopId"
            value={formData.shopId}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <label>
          Item ID:
          <input
            type="number"
            name="itemId"
            value={formData.itemId}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <label>
          Quantity:
          <input
            type="number"
            name="quantity"
            value={formData.quantity}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <label>
          Order Date:
          <input
            type="date"
            name="orderDate"
            value={formData.orderDate}
            onChange={handleChange}
            required
          />
        </label>

        <div className="form-buttons">
          <button type="submit">{order ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default OrderDetailsForm;
