import React, { useState, useEffect } from "react";
import shopApi from "../../api/shopApi";
import mallApi from "../../api/mallApi";           // assume similar API files exist
import shopOwnerApi from "../../api/shopOwnerApi";
import employeeApi from "../../api/employeeApi";

const ShopForm = ({ shop, onClose, onSubmit }) => {
  // Initialize form state with either shop data or empty/default values
  const [formData, setFormData] = useState({
    shopName: "",
    shopCategory: "",
    customers: "",
    shopStatus: "",
    leaseStatus: "",
    mall: null,
    shopOwner: null,
    shopEmployee: null,
  });

  const [malls, setMalls] = useState([]);
  const [shopOwners, setShopOwners] = useState([]);
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    // Load dropdown data on mount
    const fetchDropdowns = async () => {
      try {
        const [mallRes, ownerRes, employeeRes] = await Promise.all([
          mallApi.getAllMalls(),
          shopOwnerApi.getAllShopOwners(),
          employeeApi.getAllEmployees(),
        ]);
        setMalls(mallRes.data);
        setShopOwners(ownerRes.data);
        setEmployees(employeeRes.data);
      } catch (error) {
        console.error("Failed to fetch dropdown data", error);
      }
    };

    fetchDropdowns();
  }, []);

  useEffect(() => {
    if (shop) {
      // Map existing shop data into formData shape
      setFormData({
        shopName: shop.shopName || "",
        shopCategory: shop.shopCategory || "",
        customers: shop.customers || "",
        shopStatus: shop.shopStatus || "",
        leaseStatus: shop.leaseStatus || "",
        mall: shop.mall ? shop.mall.mallId || shop.mall.id || shop.mall : null,
        shopOwner: shop.shopOwner
          ? shop.shopOwner.shopOwnerId || shop.shopOwner.id || shop.shopOwner
          : null,
        shopEmployee: shop.shopEmployee
          ? shop.shopEmployee.employeeId || shop.shopEmployee.id || shop.shopEmployee
          : null,
      });
    } else {
      // Clear form for new shop
      setFormData({
        shopName: "",
        shopCategory: "",
        customers: "",
        shopStatus: "",
        leaseStatus: "",
        mall: null,
        shopOwner: null,
        shopEmployee: null,
      });
    }
  }, [shop]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSelectChange = (e) => {
    const { name, value } = e.target;
    // Save as integer or null
    setFormData((prev) => ({
      ...prev,
      [name]: value ? parseInt(value, 10) : null,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare payload
    const payload = {
      shopName: formData.shopName,
      shopCategory: formData.shopCategory,
      customers: formData.customers,
      shopStatus: formData.shopStatus,
      leaseStatus: formData.leaseStatus,
      mall: formData.mall ? { mallId: formData.mall } : null,
      shopOwner: formData.shopOwner ? { shopOwnerId: formData.shopOwner } : null,
      shopEmployee: formData.shopEmployee ? { employeeId: formData.shopEmployee } : null,
    };

    try {
      if (shop && shop.shopId) {
        // Update existing shop
        await shopApi.updateShop(shop.shopId, payload);
      } else {
        // Add new shop
        await shopApi.addShop(payload);
      }
      onSubmit(); // refresh list and close form
    } catch (error) {
      console.error("Error saving shop:", error);
      alert("Failed to save shop. Please check the inputs.");
    }
  };

  return (
    <div className="shop-form-container">
      <h3>{shop ? "Edit Shop" : "Add New Shop"}</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Shop Name:
          <input
            type="text"
            name="shopName"
            value={formData.shopName}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Shop Category:
          <input
            type="text"
            name="shopCategory"
            value={formData.shopCategory}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Customers:
          <input
            type="text"
            name="customers"
            value={formData.customers}
            onChange={handleChange}
          />
        </label>

        <label>
          Shop Status:
          <input
            type="text"
            name="shopStatus"
            value={formData.shopStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Lease Status:
          <input
            type="text"
            name="leaseStatus"
            value={formData.leaseStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Mall:
          <select
            name="mall"
            value={formData.mall || ""}
            onChange={handleSelectChange}
          >
            <option value="">Select Mall</option>
            {malls.map((mall) => (
              <option key={mall.mallId || mall.id} value={mall.mallId || mall.id}>
                {mall.mallName || mall.name}
              </option>
            ))}
          </select>
        </label>

        <label>
          Shop Owner:
          <select
            name="shopOwner"
            value={formData.shopOwner || ""}
            onChange={handleSelectChange}
          >
            <option value="">Select Shop Owner</option>
            {shopOwners.map((owner) => (
              <option
                key={owner.shopOwnerId || owner.id}
                value={owner.shopOwnerId || owner.id}
              >
                {owner.name || owner.ownerName}
              </option>
            ))}
          </select>
        </label>

        <label>
          Shop Employee:
          <select
            name="shopEmployee"
            value={formData.shopEmployee || ""}
            onChange={handleSelectChange}
          >
            <option value="">Select Shop Employee</option>
            {employees.map((emp) => (
              <option
                key={emp.employeeId || emp.id}
                value={emp.employeeId || emp.id}
              >
                {emp.name}
              </option>
            ))}
          </select>
        </label>

        <div style={{ marginTop: "1em" }}>
          <button type="submit">{shop ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} style={{ marginLeft: "1em" }}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ShopForm;
