import React, { useState, useEffect } from "react";
import shopApi from "../../api/shopApi";
import mallApi from "../../api/mallApi";
import shopOwnerApi from "../../api/shopOwnerApi";
import employeeApi from "../../api/employeeApi";

const ShopForm = ({ shop, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    shopName: "",
    shopCategory: "",
    customers: "",
    shopStatus: "",
    leaseStatus: "",
    mall: null,
    shopOwner: null,
    shopEmployees: [],
  });

  const [malls, setMalls] = useState([]);
  const [shopOwners, setShopOwners] = useState([]);
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchDropdowns = async () => {
      try {
        const [mallRes, ownerRes, employeeRes] = await Promise.all([
          mallApi.getAllMalls(),
          shopOwnerApi.getAllShopOwners(),
          employeeApi.getAllEmployees(),
        ]);
        setMalls(mallRes.data);
        setShopOwners(ownerRes.data);
        setEmployees(employeeRes.data);
      } catch (error) {
        console.error("Failed to fetch dropdown data", error);
      }
    };
    fetchDropdowns();
  }, []);

  useEffect(() => {
    if (shop) {
      setFormData({
        shopName: shop.shopName || "",
        shopCategory: shop.shopCategory || "",
        customers: shop.customers || "",
        shopStatus: shop.shopStatus || "",
        leaseStatus: shop.leaseStatus || "",
        mall: shop.mall ? shop.mall.mallId || shop.mall.id : null,
        shopOwner: shop.shopOwner ? shop.shopOwner.shopOwnerId || shop.shopOwner.id : null,
        shopEmployees: shop.shopEmployees
          ? shop.shopEmployees.map(emp => emp.employeeId || emp.id)
          : [],
      });
    } else {
      setFormData({
        shopName: "",
        shopCategory: "",
        customers: "",
        shopStatus: "",
        leaseStatus: "",
        mall: null,
        shopOwner: null,
        shopEmployees: [],
      });
    }
  }, [shop]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSelectChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value ? parseInt(value, 10) : null,
    }));
  };

  const handleMultiSelectChange = (e) => {
    const options = Array.from(e.target.selectedOptions);
    const selectedIds = options.map(opt => parseInt(opt.value, 10));
    setFormData((prev) => ({
      ...prev,
      shopEmployees: selectedIds,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      shopName: formData.shopName,
      shopCategory: formData.shopCategory,
      customers: formData.customers,
      shopStatus: formData.shopStatus,
      leaseStatus: formData.leaseStatus,
      mall: formData.mall ? { mallId: formData.mall } : null,
      shopOwner: formData.shopOwner ? { shopOwnerId: formData.shopOwner } : null,
      shopEmployees: formData.shopEmployees.map(empId => ({ employeeId: empId })),
    };

    try {
      if (shop && shop.shopId) {
        await shopApi.updateShop(shop.shopId, payload);
      } else {
        await shopApi.addShop(payload);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving shop:", error);
      alert("Failed to save shop. Please check the inputs.");
    }
  };

  return (
    <div className="shop-form-container">
      <h3>{shop ? "Edit Shop" : "Add New Shop"}</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Shop Name:
          <input
            type="text"
            name="shopName"
            value={formData.shopName}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Shop Category:
          <input
            type="text"
            name="shopCategory"
            value={formData.shopCategory}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Customers:
          <input
            type="text"
            name="customers"
            value={formData.customers}
            onChange={handleChange}
          />
        </label>

        <label>
          Shop Status:
          <input
            type="text"
            name="shopStatus"
            value={formData.shopStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Lease Status:
          <input
            type="text"
            name="leaseStatus"
            value={formData.leaseStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Mall:
          <select name="mall" value={formData.mall || ""} onChange={handleSelectChange}>
            <option value="">Select Mall</option>
            {malls.map((mall) => (
              <option key={mall.mallId} value={mall.mallId}>
                {mall.mallName}
              </option>
            ))}
          </select>
        </label>

        <label>
          Shop Owner:
          <select name="shopOwner" value={formData.shopOwner || ""} onChange={handleSelectChange}>
            <option value="">Select Shop Owner</option>
            {shopOwners.map((owner) => (
              <option key={owner.shopOwnerId} value={owner.shopOwnerId}>
                {owner.name || owner.ownerName}
              </option>
            ))}
          </select>
        </label>

        <label>
          Shop Employees:
          <select
            name="shopEmployees"
            multiple
            value={formData.shopEmployees.map(String)}
            onChange={handleMultiSelectChange}
          >
            {employees.map((emp) => (
              <option key={emp.employeeId} value={emp.employeeId}>
                {emp.name}
              </option>
            ))}
          </select>
        </label>

        <div style={{ marginTop: "1em" }}>
          <button type="submit">{shop ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} style={{ marginLeft: "1em" }}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ShopForm;
