// src/components/mallAdmin/MallAdminForm.js
import React, { useState, useEffect } from "react";
import mallAdminApi from "../../api/mallAdminApi";
import "./mallAdmin.css";

const MallAdminForm = ({ mallAdmin, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
  });

  useEffect(() => {
    if (mallAdmin) {
      setFormData({
        name: mallAdmin.name || "",
        email: mallAdmin.email || "",
        phone: mallAdmin.phone || "",
      });
    } else {
      setFormData({ name: "", email: "", phone: "" });
    }
  }, [mallAdmin]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (mallAdmin && mallAdmin.id) {
        await mallAdminApi.updateMallAdmin(mallAdmin.id, formData);
      } else {
        await mallAdminApi.addMallAdmin(formData);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving mall admin:", error);
    }
  };

  return (
    <div className="mallAdmin-form-overlay">
      <form className="mallAdmin-form" onSubmit={handleSubmit}>
        <h3>{mallAdmin ? "Edit Mall Admin" : "Add Mall Admin"}</h3>

        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            autoFocus
          />
        </label>

        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </label>

        <div className="form-buttons">
          <button type="submit">{mallAdmin ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default MallAdminForm;
