// src/components/orderdetails/OrderDetailsList.js
import React, { useEffect, useState } from "react";
import orderDetailsApi from "../../api/orderDetailsApi";
import OrderDetailsForm from "./OrderDetailsForm";
import "./orderDetails.css";

const OrderDetailsList = () => {
  const [orders, setOrders] = useState([]);
  const [editingOrder, setEditingOrder] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchOrders = async () => {
    try {
      const response = await orderDetailsApi.getAllOrders();
      setOrders(response.data);
    } catch (error) {
      console.error("Failed to fetch orders:", error);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleEdit = (order) => {
    setEditingOrder(order);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this order?")) {
      try {
        await orderDetailsApi.deleteOrder(id);
        fetchOrders();
      } catch (error) {
        console.error("Failed to delete order:", error);
      }
    }
  };

  const handleFormClose = () => {
    setEditingOrder(null);
    setShowForm(false);
  };

  const handleFormSubmit = () => {
    fetchOrders();
    handleFormClose();
  };

  return (
    <div className="order-container">
      <h2>Order Details</h2>
      <button onClick={() => setShowForm(true)}>Add Order</button>

      {showForm && (
        <OrderDetailsForm
          order={editingOrder}
          onClose={handleFormClose}
          onSubmit={handleFormSubmit}
        />
      )}

      <table className="order-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer ID</th>
            <th>Shop ID</th>
            <th>Item ID</th>
            <th>Quantity</th>
            <th>Order Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(
            ({
              id,
              customerId,
              shopId,
              itemId,
              quantity,
              orderDate,
            }) => (
              <tr key={id}>
                <td>{id}</td>
                <td>{customerId}</td>
                <td>{shopId}</td>
                <td>{itemId}</td>
                <td>{quantity}</td>
                <td>{new Date(orderDate).toLocaleDateString()}</td>
                <td>
                  <button onClick={() => handleEdit({
                    id,
                    customerId,
                    shopId,
                    itemId,
                    quantity,
                    orderDate,
                  })}>
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(id)}
                    className="delete-btn"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            )
          )}
          {orders.length === 0 && (
            <tr>
              <td colSpan="7">No orders found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default OrderDetailsList;
