import React, { useEffect, useState } from "react";
import mallApi from "../api/mallApi";
import "./mall.css";

export default function MallList() {
  const [malls, setMalls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchMalls = async () => {
    try {
      setLoading(true);
      const response = await mallApi.getAllMalls();
      setMalls(response.data);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch malls");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMalls();
  }, []);

  const deleteMall = async (id) => {
    if (window.confirm("Are you sure to delete this mall?")) {
      try {
        await mallApi.deleteMall(id);
        fetchMalls();
      } catch {
        alert("Failed to delete mall");
      }
    }
  };

  if (loading) return <p>Loading malls...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Malls</h2>
      <table border="1" cellPadding="8" cellSpacing="0" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Location</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {malls.map((mall) => (
            <tr key={mall.id}>
              <td>{mall.id}</td>
              <td>{mall.name}</td>
              <td>{mall.location}</td>
              <td>{mall.description}</td>
              <td>
                <button onClick={() => alert("Implement Edit functionality")}>Edit</button>{" "}
                <button onClick={() => deleteMall(mall.id)}>Delete</button>
              </td>
            </tr>
          ))}
          {malls.length === 0 && (
            <tr>
              <td colSpan="5">No malls found</td>
            </tr>
          )}
        </tbody>
      </table>
      <button onClick={() => alert("Implement Add Mall functionality")}>Add New Mall</button>
    </div>
  );
}
