// src/api/employeeApi.js
import apiClient from "../utils/apiClient";

const employeeApi = {
  getAllEmployees: () => apiClient.get("/employees"),
  getEmployeeById: (id) => apiClient.get(`/employees/${id}`),
  addEmployee: (employee) => apiClient.post("/employees", employee),
  updateEmployee: (id, employee) => apiClient.put(`/employees/${id}`, employee),
  deleteEmployee: (id) => apiClient.delete(`/employees/${id}`),
};

export default employeeApi;
