// src/components/shops/ShopForm.js
import React, { useState, useEffect } from "react";
import shopApi from "../../api/shopApi";
import "./shop.css";

const ShopForm = ({ shop, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    location: "",
    shopOwnerId: "",
  });

  useEffect(() => {
    if (shop) {
      setFormData({
        name: shop.name || "",
        location: shop.location || "",
        shopOwnerId: shop.shopOwnerId || "",
      });
    } else {
      setFormData({ name: "", location: "", shopOwnerId: "" });
    }
  }, [shop]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (shop && shop.id) {
        await shopApi.updateShop(shop.id, formData);
      } else {
        await shopApi.addShop(formData);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving shop:", error);
    }
  };

  return (
    <div className="shop-form-overlay">
      <form className="shop-form" onSubmit={handleSubmit}>
        <h3>{shop ? "Edit Shop" : "Add Shop"}</h3>

        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            autoFocus
          />
        </label>

        <label>
          Location:
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Shop Owner ID:
          <input
            type="number"
            name="shopOwnerId"
            value={formData.shopOwnerId}
            onChange={handleChange}
            required
            min="1"
          />
        </label>

        <div className="form-buttons">
          <button type="submit">{shop ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ShopForm;
