import apiClient from "../utils/apiClient";

const itemApi = {
  getAllItems: () => apiClient.get("/api/items"),
  getItemById: (id) => apiClient.get(`/api/items/${id}`),
  addItem: (item) => apiClient.post("/api/items", item),
  updateItem: (id, item) => apiClient.put(`/api/items/${id}`, item),
  deleteItem: (id) => apiClient.delete(`/api/items/${id}`),
};

export default itemApi;
