import React, { useState, useEffect } from "react";
import mallApi from "../../api/mallApi";
import "./mall.css";

export default function MallForm({ mallId, onSuccess, onCancel }) {
  const [mall, setMall] = useState({
    mallName: "",
    location: "",
    categories: "",
  });

  const [error, setError] = useState(null);
  const [saving, setSaving] = useState(false); // disable submit while saving

  // Load mall details for editing if mallId exists
  useEffect(() => {
    if (mallId) {
      mallApi
        .getMallById(mallId)
        .then((res) => {
          setMall({
            mallName: res.data.mallName || "",
            location: res.data.location || "",
            categories: res.data.categories || "",
          });
        })
        .catch(() => setError("Failed to load mall details"));
    }
  }, [mallId]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setMall((prev) => ({ ...prev, [name]: value }));
  };

  // Submit form for add or update
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Basic validation
    if (!mall.mallName.trim() || !mall.location.trim()) {
      setError("Mall Name and Location are required.");
      return;
    }

    try {
      setSaving(true);
      if (mallId) {
        // Update mall
        await mallApi.updateMall(mallId, mall);
      } else {
        // Add mall
        await mallApi.addMall(mall);
      }
      setSaving(false);
      onSuccess();
    } catch {
      setSaving(false);
      setError("Failed to save mall. Please try again.");
    }
  };

  return (
    <div className="mall-form-container">
      <h2>{mallId ? "Edit Mall" : "Add Mall"}</h2>

      {error && <p className="error-message" style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="mallName">Mall Name*:</label>
          <input
            type="text"
            id="mallName"
            name="mallName"
            value={mall.mallName}
            onChange={handleChange}
            required
            placeholder="Enter mall name"
            disabled={saving}
          />
        </div>

        <div className="form-group">
          <label htmlFor="location">Location*:</label>
          <input
            type="text"
            id="location"
            name="location"
            value={mall.location}
            onChange={handleChange}
            required
            placeholder="Enter location"
            disabled={saving}
          />
        </div>

        <div className="form-group">
          <label htmlFor="categories">Categories:</label>
          <textarea
            id="categories"
            name="categories"
            value={mall.categories}
            onChange={handleChange}
            placeholder="Enter categories or description"
            rows={4}
            disabled={saving}
          />
        </div>

        <div className="form-buttons" style={{ marginTop: "1rem" }}>
          <button type="submit" disabled={saving}>
            {saving ? (mallId ? "Updating..." : "Adding...") : (mallId ? "Update" : "Add")}
          </button>{" "}
          <button type="button" onClick={onCancel} disabled={saving}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
