import React, { useEffect, useState } from "react";
import shopOwnerApi from "../../api/shopOwnerApi";
import ShopOwnerForm from "./ShopOwnerForm";
import "./shopOwner.css";

const ShopOwnerList = () => {
  const [shopOwners, setShopOwners] = useState([]);
  const [editingShopOwner, setEditingShopOwner] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchShopOwners = async () => {
    try {
      const response = await shopOwnerApi.getAllShopOwners();
      const data = response.data || [];
      setShopOwners(data.filter((owner) => owner && owner.shopOwnerId));
    } catch (error) {
      console.error("Failed to fetch shop owners:", error);
    }
  };

  useEffect(() => {
    fetchShopOwners();
  }, []);

  const handleEdit = (shopOwner) => {
    setEditingShopOwner(shopOwner);
    setShowForm(true);
  };

  const handleDelete = async (shopOwnerId) => {
    if (window.confirm("Are you sure you want to delete this shop owner?")) {
      try {
        await shopOwnerApi.deleteShopOwner(shopOwnerId);
        fetchShopOwners();
      } catch (error) {
        console.error("Failed to delete shop owner:", error);
      }
    }
  };

  const handleFormClose = () => {
    setEditingShopOwner(null);
    setShowForm(false);
  };

  const handleFormSubmit = () => {
    fetchShopOwners();
    handleFormClose();
  };

  return (
    <div className="shopowner-container">
      <h2>Shop Owner List</h2>
      <button onClick={() => {
        setEditingShopOwner(null);
        setShowForm(true);
      }}>Add Shop Owner</button>

      {showForm && (
        <ShopOwnerForm
          shopOwner={editingShopOwner}
          onClose={handleFormClose}
          onSubmit={handleFormSubmit}
        />
      )}

      <table className="shopowner-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>DOB</th>
            <th>Address</th>
            <th>Mall</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {shopOwners.length ? (
            shopOwners.map((owner) => (
              <tr key={owner.shopOwnerId}>
                <td>{owner.shopOwnerId}</td>
                <td>{owner.name}</td>
                <td>{owner.dob}</td>
                <td>{owner.address}</td>
                <td>{owner.mall?.mallName || "N/A"}</td>
                <td>
                  <button onClick={() => handleEdit(owner)}>Edit</button>
                  <button
                    onClick={() => handleDelete(owner.shopOwnerId)}
                    className="delete-btn"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No shop owners found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ShopOwnerList;
