// src/api/userApi.js
import apiClient from "../utils/apiClient";

const userApi = {
  getAllUsers: () => apiClient.get("/api/users"),
  getUserById: (id) => apiClient.get(`/api/users/${id}`),
  addUser: (user) => apiClient.post("/api/users", user),
  updateUser: (id, user) => apiClient.put(`/api/users/${id}`, user),
  deleteUser: (id) => apiClient.delete(`/api/users/${id}`),
};

export default userApi;
