// src/components/shops/ShopList.js
import React, { useEffect, useState } from "react";
import shopApi from "../../api/shopApi";
import ShopForm from "./ShopForm";
import "./shop.css";

const ShopList = () => {
  const [shops, setShops] = useState([]);
  const [editingShop, setEditingShop] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchShops = async () => {
    try {
      const response = await shopApi.getAllShops();
      setShops(response.data);
    } catch (error) {
      console.error("Failed to fetch shops:", error);
    }
  };

  useEffect(() => {
    fetchShops();
  }, []);

  const handleEdit = (shop) => {
    setEditingShop(shop);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this shop?")) {
      try {
        await shopApi.deleteShop(id);
        fetchShops();
      } catch (error) {
        console.error("Failed to delete shop:", error);
      }
    }
  };

  const handleFormClose = () => {
    setEditingShop(null);
    setShowForm(false);
  };

  const handleFormSubmit = () => {
    fetchShops();
    handleFormClose();
  };

  return (
    <div className="shop-container">
      <h2>Shop List</h2>
      <button onClick={() => setShowForm(true)}>Add Shop</button>

      {showForm && (
        <ShopForm
          shop={editingShop}
          onClose={handleFormClose}
          onSubmit={handleFormSubmit}
        />
      )}

      <table className="shop-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Location</th>
            <th>Shop Owner ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {shops.map(({ id, name, location, shopOwnerId }) => (
            <tr key={id}>
              <td>{id}</td>
              <td>{name}</td>
              <td>{location}</td>
              <td>{shopOwnerId}</td>
              <td>
                <button onClick={() => handleEdit({ id, name, location, shopOwnerId })}>
                  Edit
                </button>
                <button onClick={() => handleDelete(id)} className="delete-btn">
                  Delete
                </button>
              </td>
            </tr>
          ))}
          {shops.length === 0 && (
            <tr>
              <td colSpan="5">No shops found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ShopList;
