import React, { useEffect, useState } from "react";
import shopApi from "../../api/shopApi";
import ShopForm from "./ShopForm";
import "./shop.css";

const ShopList = () => {
  const [shops, setShops] = useState([]);
  const [editingShop, setEditingShop] = useState(null);
  const [showForm, setShowForm] = useState(false);

  // Normalize shop object to pass to ShopForm
  const normalizeShop = (shop) => ({
    shopId: shop.shopId,
    shopName: shop.shopName,
    shopCategory: shop.shopCategory,
    customers: shop.customers,
    shopStatus: shop.shopStatus,
    leaseStatus: shop.leaseStatus,
    shopOwnerId: shop.shopOwner ? shop.shopOwner.id : "",
    mallId: shop.mall ? shop.mall.id : "",
    shopEmployeeId: shop.shopEmployee ? shop.shopEmployee.id : "",
    // Keep nested objects for table display
    shopOwner: shop.shopOwner,
    mall: shop.mall,
    shopEmployee: shop.shopEmployee,
  });

  const fetchShops = async () => {
    try {
      const response = await shopApi.getAllShops();
      // Normalize all shops to include flattened IDs
      const normalizedShops = response.data.map(normalizeShop);
      setShops(normalizedShops);
    } catch (error) {
      console.error("Failed to fetch shops:", error);
    }
  };

  useEffect(() => {
    fetchShops();
  }, []);

  const handleEdit = (shop) => {
    setEditingShop(shop);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this shop?")) {
      try {
        await shopApi.deleteShop(id);
        fetchShops();
      } catch (error) {
        console.error("Failed to delete shop:", error);
      }
    }
  };

  const handleFormClose = () => {
    setEditingShop(null);
    setShowForm(false);
  };

  const handleFormSubmit = () => {
    fetchShops();
    handleFormClose();
  };

  return (
    <div className="shop-container">
      <h2>Shop List</h2>
      <button onClick={() => setShowForm(true)}>Add Shop</button>

      {showForm && (
        <ShopForm
          shop={editingShop}
          onClose={handleFormClose}
          onSubmit={handleFormSubmit}
        />
      )}

      <table className="shop-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Shop Name</th>
            <th>Category</th>
            <th>Customers</th>
            <th>Status</th>
            <th>Lease Status</th>
            <th>Shop Owner</th>
            <th>Mall</th>
            <th>Shop Employee</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {shops.length === 0 ? (
            <tr>
              <td colSpan="10">No shops found.</td>
            </tr>
          ) : (
            shops.map(
              ({
                shopId,
                shopName,
                shopCategory,
                customers,
                shopStatus,
                leaseStatus,
                shopOwner,
                mall,
                shopEmployee,
              }) => (
                <tr key={shopId}>
                  <td>{shopId}</td>
                  <td>{shopName}</td>
                  <td>{shopCategory}</td>
                  <td>{customers}</td>
                  <td>{shopStatus}</td>
                  <td>{leaseStatus}</td>
                  <td>{shopOwner ? shopOwner.name || shopOwner.id : "N/A"}</td>
                  <td>{mall ? mall.mallName || mall.id : "N/A"}</td>
                  <td>{shopEmployee ? shopEmployee.name || shopEmployee.id : "N/A"}</td>
                  <td>
                    <button onClick={() => handleEdit(normalizeShop({
                      shopId,
                      shopName,
                      shopCategory,
                      customers,
                      shopStatus,
                      leaseStatus,
                      shopOwner,
                      mall,
                      shopEmployee,
                    }))}>Edit</button>
                    <button onClick={() => handleDelete(shopId)} className="delete-btn">
                      Delete
                    </button>
                  </td>
                </tr>
              )
            )
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ShopList;
