import React, { useState, useEffect } from "react";
import shopApi from "../../api/shopApi";
import shopOwnerApi from "../../api/shopOwnerApi";
import mallApi from "../../api/mallApi";
import employeeApi from "../../api/employeeApi"; // You need this API to fetch employees
import "./shop.css";

const ShopForm = ({ shop, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    shopName: "",
    shopCategory: "",
    customers: "",
    shopStatus: "",
    leaseStatus: "",
    shopOwnerId: "",
    mallId: "",
    shopEmployeeId: "",
  });

  const [shopOwners, setShopOwners] = useState([]);
  const [malls, setMalls] = useState([]);
  const [employees, setEmployees] = useState([]);

  // Fetch ShopOwners, Malls, and Employees for dropdowns
  useEffect(() => {
    shopOwnerApi.getAllShopOwners()
      .then(res => setShopOwners(res.data))
      .catch(() => alert("Failed to load shop owners"));

    mallApi.getAllMalls()
      .then(res => setMalls(res.data))
      .catch(() => alert("Failed to load malls"));

    employeeApi.getAllEmployees()
      .then(res => setEmployees(res.data))
      .catch(() => alert("Failed to load employees"));
  }, []);

  // Populate form for edit
  useEffect(() => {
    if (shop) {
      setFormData({
        shopName: shop.shopName || "",
        shopCategory: shop.shopCategory || "",
        customers: shop.customers || "",
        shopStatus: shop.shopStatus || "",
        leaseStatus: shop.leaseStatus || "",
        shopOwnerId: shop.shopOwner ? shop.shopOwner.id : "",
        mallId: shop.mall ? shop.mall.id : "",
        shopEmployeeId: shop.shopEmployee ? shop.shopEmployee.id : "",
      });
    } else {
      setFormData({
        shopName: "",
        shopCategory: "",
        customers: "",
        shopStatus: "",
        leaseStatus: "",
        shopOwnerId: "",
        mallId: "",
        shopEmployeeId: "",
      });
    }
  }, [shop]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.shopName || !formData.shopOwnerId || !formData.mallId || !formData.shopEmployeeId) {
      alert("Please fill in required fields: Shop Name, Shop Owner, Mall, and Shop Employee.");
      return;
    }

    // Prepare payload for backend
    const payload = {
      shopName: formData.shopName,
      shopCategory: formData.shopCategory,
      customers: formData.customers,
      shopStatus: formData.shopStatus,
      leaseStatus: formData.leaseStatus,
      shopOwner: { id: formData.shopOwnerId },
      mall: { id: formData.mallId },
      shopEmployee: { id: formData.shopEmployeeId },
    };

    try {
      if (shop && shop.shopId) {
        await shopApi.updateShop(shop.shopId, payload);
      } else {
        await shopApi.addShop(payload);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving shop:", error);
    }
  };

  return (
    <div className="shop-form-overlay">
      <form className="shop-form" onSubmit={handleSubmit}>
        <h3>{shop ? "Edit Shop" : "Add Shop"}</h3>

        <label>
          Shop Name*:
          <input
            type="text"
            name="shopName"
            value={formData.shopName}
            onChange={handleChange}
            required
            autoFocus
          />
        </label>

        <label>
          Shop Category:
          <input
            type="text"
            name="shopCategory"
            value={formData.shopCategory}
            onChange={handleChange}
          />
        </label>

        <label>
          Customers:
          <input
            type="text"
            name="customers"
            value={formData.customers}
            onChange={handleChange}
          />
        </label>

        <label>
          Shop Status:
          <input
            type="text"
            name="shopStatus"
            value={formData.shopStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Lease Status:
          <input
            type="text"
            name="leaseStatus"
            value={formData.leaseStatus}
            onChange={handleChange}
          />
        </label>

        <label>
          Shop Owner*:
          <select
            name="shopOwnerId"
            value={formData.shopOwnerId}
            onChange={handleChange}
            required
          >
            <option value="">Select Shop Owner</option>
            {shopOwners.map(owner => (
              <option key={owner.id} value={owner.id}>
                {owner.name}
              </option>
            ))}
          </select>
        </label>

        <label>
          Mall*:
          <select
            name="mallId"
            value={formData.mallId}
            onChange={handleChange}
            required
          >
            <option value="">Select Mall</option>
            {malls.map(mall => (
              <option key={mall.id} value={mall.id}>
                {mall.mallName}
              </option>
            ))}
          </select>
        </label>

        <label>
          Shop Employee*:
          <select
            name="shopEmployeeId"
            value={formData.shopEmployeeId}
            onChange={handleChange}
            required
          >
            <option value="">Select Shop Employee</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>
                {emp.name}
              </option>
            ))}
          </select>
        </label>

        <div className="form-buttons">
          <button type="submit">{shop ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ShopForm;
