// src/components/employee/EmployeeForm.js
import React, { useState, useEffect } from "react";
import employeeApi from "../../api/employeeApi";
import "./employee.css";

const EmployeeForm = ({ employee, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    position: "",
  });

  useEffect(() => {
    if (employee) {
      setFormData({
        name: employee.name || "",
        email: employee.email || "",
        phone: employee.phone || "",
        position: employee.position || "",
      });
    } else {
      setFormData({ name: "", email: "", phone: "", position: "" });
    }
  }, [employee]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (employee && employee.id) {
        await employeeApi.updateEmployee(employee.id, formData);
      } else {
        await employeeApi.addEmployee(formData);
      }
      onSubmit();
    } catch (error) {
      console.error("Error saving employee:", error);
    }
  };

  return (
    <div className="employee-form-overlay">
      <form className="employee-form" onSubmit={handleSubmit}>
        <h3>{employee ? "Edit Employee" : "Add Employee"}</h3>

        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            autoFocus
          />
        </label>

        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Position:
          <input
            type="text"
            name="position"
            value={formData.position}
            onChange={handleChange}
            required
          />
        </label>

        <div className="form-buttons">
          <button type="submit">{employee ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EmployeeForm;
