import React, { useState, useEffect } from "react";
import itemApi from "../../api/itemApi";
import "./item.css";

export default function ItemForm({ item, onClose }) {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    shopId: "",
  });

  useEffect(() => {
    if (item) {
      setFormData({
        name: item.name || "",
        description: item.description || "",
        price: item.price || "",
        shopId: item.shopId || "",
      });
    }
  }, [item]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.price || !formData.shopId) {
      alert("Please fill in all required fields (name, price, shop ID).");
      return;
    }

    if (item) {
      itemApi.updateItem(item.id, formData)
        .then(() => {
          alert("Item updated successfully.");
          onClose();
        })
        .catch(() => alert("Failed to update item."));
    } else {
      itemApi.addItem(formData)
        .then(() => {
          alert("Item added successfully.");
          onClose();
        })
        .catch(() => alert("Failed to add item."));
    }
  };

  return (
    <div className="form-container">
      <h3>{item ? "Edit Item" : "Add New Item"}</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Name*:
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </label>
        <label>
          Description:
          <textarea name="description" value={formData.description} onChange={handleChange} />
        </label>
        <label>
          Price*:
          <input type="number" step="0.01" name="price" value={formData.price} onChange={handleChange} />
        </label>
        <label>
          Shop ID*:
          <input type="number" name="shopId" value={formData.shopId} onChange={handleChange} />
        </label>
        <div className="form-buttons">
          <button type="submit">{item ? "Update" : "Add"}</button>
          <button type="button" onClick={onClose}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
